import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import CourseCard from '../components/Courses/CourseCard'; // 确保路径正确
import SearchBar from '../components/SearchBar.js';
import TitleSearchBar from '../components/TitleSearchBar'; // 引入 TitleSearchBar 组件

import { getEnrolledCourses } from '../api/course'; // 引入 getEnrolledCourses 函数

const HomePage = () => {
  const [enrolledCourses, setEnrolledCourses] = useState([]);
  const { user } = useAuth(); // 使用 useAuth 钩子获取用户数据

  useEffect(() => {
    if (user) {
      fetchEnrolledCourses();
    }
  }, [user]);

  const fetchEnrolledCourses = async () => {
    try {
      const courses = await getEnrolledCourses(user._id);
      setEnrolledCourses(courses);
    } catch (error) {
      console.error('Error fetching enrolled courses:', error);
    }
  };

  // Hardcoded information for logged-in users
  const loggedInInfo = (
    <div>
      <h2>Exclusive Updates for Members</h2>
      <p>As a registered member, you have access to expert tips, and special online events. Stay tuned for more updates!</p>
    </div>
  );

  return (
    <div>
      <h1>Welcome to Chess Online Education</h1>
      <p>Learn chess with the best courses and instructors online.</p>
      <SearchBar />
      <TitleSearchBar /> {/* 添加 TitleSearchBar 组件 */}

      {/* Display exclusive information for logged-in users */}
      {user && loggedInInfo}

      {user && enrolledCourses.length > 0 && (
        <div>
          <h2>Continue Study</h2>
          <div>
            {enrolledCourses.map(course => (
              <CourseCard 
                key={course._id} 
                course={course} 
                userRole={user.role} 
                isEnrolled={true} 
              />
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default HomePage;
