import CourseCard from '../components/Courses/CourseCard'; 
import './CartPage.css';
const CartPage = () => {
  const [enrolledCourses, setEnrolledCourses] = useState([]);
  const { user } = useAuth(); // 使用 useAuth 钩子获取用户数据

  useEffect(() => {
    if (user) {
      fetchEnrolledCourses();
    }
  }, [user]);

  const username = user?.username; // 直接访问用户角色

